import React, {memo} from 'react';
import Counter from './Counter';

const Player = ({name, removePlayer, id, score, changeScore }) => {
    return (
      <div className="player">
        {console.log(name + ' rendered')}
        <span className="player-name">
          <button className="remove-player" onClick={() => removePlayer(id)}>âœ–</button>
          {name}
        </span>

  {/*counter gets a prop called changeScore, which comes from app.js
  When a button in the counter is clicked, the function will be executed */}
        <Counter 
        score={'score'}
        id={id}
        changeScore={changeScore}
        />
      </div>
    );
  }

  const playerPropsAreEqual = (prevProps, nextProps) => {
    //Changes a player instance only when a change in the score prop is detected
    return prevProps.score === nextProps.score;

  }

  export default memo(Player, playerPropsAreEqual);
