import React, { useEffect, useState } from "react";
import axios from 'axios';
import SearchForm from './Components/SearchForm';
import GifList from "./Components/GifList";

/* We wire the app up to the giphy api results and display them as a list of animated gifs. 
In our app, the app component is the container responsible for rendering the markup of the app, as well as child components. 

The app component is aware of the app as a whole. Initializes and updates the gif state with the response data, but it will also provide the data and behavior to its child components. 
*/
function App() {
  const [gifs, setGifs] = useState([]) ;

  useEffect(() => {
  axios.get("https://api.giphy.com/v1/gifs/trending?api_key=4js8zLuZ7cD54R9jHuK17V790TFflJTa&limit=24&rating=g")
  .then(function (response) {
    // handle success
  })
  .catch(function (error) {
    // handle error
    console.log("Error fetching and parsing data");
  })

}, []);




  return (
    <div>
      <div className="main-header">
        <div className="inner">
          <h1 className="main-title">GifSearch</h1>
          <SearchForm />
        </div>
      </div>
      <div className="main-content">
        <GifList />
      </div>
    </div>
  );
}

export default App;






